mapper.select:
SELECT * FROM analysis_report
WHERE 1=1

    AND plan_id = #{req.planId}


    AND report_name LIKE CONCAT('%', #{req.reportName}, '%')


    AND status = #{req.status}

ORDER BY create_time DESC
LIMIT #{req.pageSize} OFFSET #{req.offset}

SELECT count(1) FROM analysis_report
WHERE 1=1

    AND plan_id = #{req.planId}


    AND report_name LIKE CONCAT('%', #{req.reportName}, '%')


    AND status = #{req.status}
