mapper.select:
SELECT * FROM experiment_plan
WHERE 1=1

    AND plan_name LIKE CONCAT('%', #{req.planName}, '%')


    AND status = #{req.status}


    AND owner_id = #{req.ownerId}

ORDER BY create_time DESC
LIMIT #{req.pageSize} OFFSET #{req.offset}

SELECT count(1) FROM experiment_plan
WHERE 1=1

    AND plan_name LIKE CONCAT('%', #{req.planName}, '%')


    AND status = #{req.status}


    AND owner_id = #{req.ownerId}
